import { createSelector } from '@ngrx/store';
import { selectNewsState } from './index';
/**
 * Get News Section Selector
 */

export var getNewsSection = createSelector(selectNewsState, function (news) {
  return news.sectionNews;
});
/**
 * Get Filter SubSection Section Selector
 */

export var getFilterSubSection = createSelector(selectNewsState, function (news) {
  return news.sectionNews.filter(function (item) {
    // section of news selected
    if (!news.filterNewsSection.length) {
      return item;
    } else {
      // subsection of news selected
      if (item.subsection === news.filterNewsSection) {
        return item;
      }
    }
  });
});
/**
 * Get Current News Section Selector
 */

export var getCurrentNewsSection = createSelector(selectNewsState, function (news) {
  return news.currentNewsSection;
});
