import { createSelector } from '@ngrx/store';
/**
 * Get App State
 */

export const getAppState = state => state;
/**
 * Select Sections State
 */

export const selectSectionsState = createSelector(getAppState, state => state.sections);
/**
 * Select News State
 */

export const selectNewsState = createSelector(getAppState, state => state.news);
