import { createSelector } from '@ngrx/store';
import { selectNewsState } from './index';
/**
 * Get News Section Selector
 */

export const getNewsSection = createSelector(selectNewsState, news => news.sectionNews);
/**
 * Get Filter SubSection Section Selector
 */

export const getFilterSubSection = createSelector(selectNewsState, news => news.sectionNews.filter(item => {
  // section of news selected
  if (!news.filterNewsSection.length) {
    return item;
  } else {
    // subsection of news selected
    if (item.subsection === news.filterNewsSection) {
      return item;
    }
  }
}));
/**
 * Get Current News Section Selector
 */

export const getCurrentNewsSection = createSelector(selectNewsState, news => news.currentNewsSection);
